CREATE OR REPLACE FUNCTION obter_inf_nasc_nom (nr_seq_nascimento_p bigint, nr_atendimento_p bigint, ie_tipo_informacao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1);


BEGIN

	select 	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ds_retorno_w
	from	atend_vacina_teste a,
			vacina_teste_rn b
	where	a.nr_seq_nascimento = nr_seq_nascimento_p
	and		a.nr_atendimento = nr_atendimento_p
	and		b.nr_sequencia = a.nr_seq_teste_vacina
	and		coalesce(b.ie_tipo_vacina,'N') = ie_tipo_informacao_p;

return	ds_retorno_w;

end;
