CREATE OR REPLACE FUNCTION obter_inf_pac_pront (CD_PESSOA_FISICA_P bigint, IE_OPCAO_P text) RETURNS timestamp AS $body$
DECLARE

								
ds_retorno_w	timestamp;
qt_retorno_w 	varchar(255);

/*

DE - Data de Entrada 1º atendimento do paciente

DAU - Data de Alta do último atendimento, ou sysdate se o atendimento estiver aberto

*/
							


BEGIN

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
		
	if (ie_opcao_p = 'DE') then
	
		SELECT	min(a.dt_entrada)
		into STRICT 	ds_retorno_w
		FROM 	atendimento_paciente a
		where 	a.cd_pessoa_fisica = cd_pessoa_fisica_p;
		
	elsif (ie_opcao_p = 'DAU') then
	
		select 	count(*) QT_REG
		into STRICT	qt_retorno_w
		from 	atendimento_paciente a
		where 	a.cd_pessoa_fisica = cd_pessoa_fisica_p
		and	coalesce(a.dt_alta::text, '') = '';
		
		if (qt_retorno_w > 0) then
		
			ds_retorno_w := clock_timestamp();
			
		else
		
		select 	max(a.dt_alta)
		into STRICT	ds_retorno_w
		from 	atendimento_paciente a
		where 	a.cd_pessoa_fisica = cd_pessoa_fisica_p;
		
		end if;
		
	end if;	
	
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
