SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_inf_risco_medic ( nr_prescricao_p bigint, cd_material_p bigint, nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255) := '';

--ie_opcao_p  = 'O' Observação;
--ie_opcao_p  = 'R' Orientação;
BEGIN

if (ie_opcao_p = 'O') then

	if (coalesce(nr_prescricao_p,0) > 0) and (coalesce(nr_sequencia_p,0) > 0) then

		select   max(substr(b.ds_inf_adicional,1,255))
		into STRICT	  ds_retorno_w
		from	  prescr_medica_erro b
		where	  b.nr_seq_medic  = nr_sequencia_p
		and	  b.nr_prescricao = nr_prescricao_p;


		if (coalesce(ds_retorno_w,'XPTO') = 'XPTO') then

			select    max(substr(c.ds_observacao,1,255))
			into STRICT	  ds_retorno_w
			from	  prescr_material a,
				  medicamento_risco c
			where	  a.cd_material = c.cd_material
			and	  a.nr_sequencia = nr_sequencia_p
			and	  a.nr_prescricao = nr_prescricao_p
			and	  a.ie_agrupador  = 1
			and	  coalesce(c.ie_nao_conformidade,'N') = 'S'
			and	  coalesce(c.ie_situacao, 'A') = 'A';

		end if;
	end if;

elsif (ie_opcao_p = 'R') then

		select    max(substr(c.ds_orientacao,1,255))
		into STRICT	  ds_retorno_w
		from	  prescr_material a,
			  medicamento_risco c
		where	  a.cd_material = c.cd_material
		and	  a.nr_sequencia = nr_sequencia_p
		and	  a.nr_prescricao = nr_prescricao_p
		and	  a.ie_agrupador  = 1
		and	  coalesce(c.ie_nao_conformidade,'N') = 'S'
		and	  coalesce(c.ie_situacao, 'A') = 'A';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
