CREATE OR REPLACE FUNCTION obter_inf_san_exame ( nr_seq_exame_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ie_sorologia_w	varchar(1);
ie_retorno_w	varchar(1);


BEGIN

if (nr_seq_exame_p IS NOT NULL AND nr_seq_exame_p::text <> '') then
	select	ie_sorologia
	into STRICT	ie_sorologia_w
	from   	san_exame
	where 	nr_sequencia = nr_seq_exame_p;

	if (ie_opcao_p = 'S') then
		ie_retorno_w := ie_sorologia_w;
	end if;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
