CREATE OR REPLACE FUNCTION obter_inf_saude_mulher (nr_atendimento_p bigint, ie_tipo_info_p text, cd_pessoa_fisica_p text default null) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(1) := 'N';
nr_retorno_w      		real := null;
cd_pessoa_fisica_w		pessoa_fisica.cd_pessoa_fisica%type;

-- G = Gestante

-- A = Amamentando

-- P = Pregnant

-- PW = Pregnant Weeks
BEGIN

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	cd_pessoa_fisica_w := cd_pessoa_fisica_p;
elsif (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then
	select	obter_pessoa_atendimento(nr_atendimento_p,'C')
	into STRICT	cd_pessoa_fisica_w
	;
end if;

if (cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '') then
	
	if (ie_tipo_info_p = 'G') then
		
		select coalesce(max('S'),'N')
		into STRICT   ds_retorno_w
		from historico_saude_mulher
		where (dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
		and ie_situacao = 'A'
		and	cd_pessoa_fisica = cd_pessoa_fisica_w		
		and (dt_inicio_gestacao IS NOT NULL AND dt_inicio_gestacao::text <> '')
		and dt_inicio_gestacao <= clock_timestamp()
		and coalesce(dt_fim_gestacao::text, '') = '';
		
	elsif (ie_tipo_info_p = 'A') then
		
		select coalesce(max('S'),'N')
		into STRICT   ds_retorno_w
		from historico_saude_mulher
		where (dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
		and ie_situacao = 'A'
		and	cd_pessoa_fisica = cd_pessoa_fisica_w
		and (dt_inicio_amamentacao IS NOT NULL AND dt_inicio_amamentacao::text <> '')
		and dt_inicio_amamentacao <= clock_timestamp()
		and coalesce(dt_fim_amamentacao::text, '') = '';

	elsif (ie_tipo_info_p = 'P') then

		select coalesce(max('S'),'N')
		into STRICT   ds_retorno_w
		from historico_saude_mulher
		where (dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
		and ie_pac_gravida = 'S'
		and ie_situacao = 'A'
		and	cd_pessoa_fisica = cd_pessoa_fisica_w;

	elsif (ie_tipo_info_p = 'PW') then

		select max(qt_sem_ig_cronologica)
		into STRICT   nr_retorno_w
		from historico_saude_mulher
		where (dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
		and ie_situacao = 'A'
		and ie_pac_gravida = 'S'
		and	cd_pessoa_fisica = cd_pessoa_fisica_w;

	end if;

end if;

if (nr_retorno_w IS NOT NULL AND nr_retorno_w::text <> '') then
    return	nr_retorno_w;
else
    return  ds_retorno_w;
end if;

return	ds_retorno_w;	

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
