SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_inf_sessao (ie_informacao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(254);

/*
0	- machine
1	- osuser
*/
BEGIN

if (ie_informacao_p = 0) then
	select	max(machine)
	into STRICT	ds_retorno_w
	from 	v$session
	where	audsid = (SELECT max(userenv('sessionid')) );
elsif (ie_informacao_p = 1) then
	select	max(osuser)
	into STRICT	ds_retorno_w
	from 	v$session
	where	audsid = (SELECT max(userenv('sessionid')) );
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
