SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_inf_triagem_pa (nr_atendimento_p bigint, informacao_p text) RETURNS varchar AS $body$
DECLARE



ds_retorno_w	varchar(255);
ds_observacao_w varchar(1000);
/*
informacao: Q - ds_queixa_princ  da TRIAGEM_PRONTO_ATEND
	F -  DS_FATOR_DETERMINANTE da TRIAGEM_PRONTO_ATEND
	O - DS_OBSERVACAO da TRIAGEM_PRONTO_ATEND
*/
BEGIN

if (coalesce(nr_atendimento_p,0) > 0) then

	if (informacao_p = 'Q') then
		select  max(ds_queixa_princ)
		into STRICT	ds_retorno_w
		from	triagem_pronto_atend
		where 	nr_atendimento = nr_atendimento_p
		and    dt_atualizacao = (SELECT  max(dt_atualizacao)
				from	triagem_pronto_atend
				where 	nr_atendimento = nr_atendimento_p);

	elsif (informacao_p = 'F') then
		select  max(ds_fator_determinante)
		into STRICT	ds_retorno_w
		from	triagem_pronto_atend
		where 	nr_atendimento = nr_atendimento_p
		and    dt_atualizacao = (SELECT  max(dt_atualizacao)
				from	triagem_pronto_atend
				where 	nr_atendimento = nr_atendimento_p);

	elsif (informacao_p = 'O') then
		select  max(ds_observacao)
		into STRICT	ds_observacao_w
		from	triagem_pronto_atend
		where 	nr_atendimento = nr_atendimento_p
		and    dt_atualizacao = (SELECT  max(dt_atualizacao)
				from	triagem_pronto_atend
				where 	nr_atendimento = nr_atendimento_p);
	end if;
end if;

if (informacao_p = 'O') then
	return	substr(ds_observacao_w,1,1000);
else
	return	substr(ds_retorno_w,1,255);
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
