SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_inf_unidade (nr_seq_interno_p integer, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE



/*
'S'  = Status
'I'  = Data in√≠cio
'F'  = Data fim
'P' =  Data de pausa
'N'


*/
ds_retorno_w	varchar(100);
nr_sequencia_w	bigint;

BEGIN

SELECT 	MAX(NR_SEQUENCIA)
INTO STRICT	nr_sequencia_w
FROM   	SL_UNID_ATEND
WHERE 	nr_seq_unidade  = nr_seq_interno_p;

IF (ie_opcao_p = 'S') THEN
	SELECT 	MAX(IE_STATUS_SERV)
	INTO STRICT	ds_retorno_w
	FROM   	SL_UNID_ATEND
	WHERE 	nr_sequencia = nr_sequencia_w;

ELSIF (ie_opcao_p = 'I') THEN
	SELECT 	MAX(DT_INICIO)
	INTO STRICT	ds_retorno_w
	FROM   	SL_UNID_ATEND
	WHERE 	nr_sequencia = nr_sequencia_w;


ELSIF (ie_opcao_p = 'F') THEN
	SELECT	MAX(DT_FIM)
	INTO STRICT	ds_retorno_w
	FROM   	SL_UNID_ATEND
	WHERE 	nr_sequencia = nr_sequencia_w;

ELSIF (ie_opcao_p = 'P') THEN
	SELECT 	MAX(DT_PAUSA_SERVICO)
	INTO STRICT	ds_retorno_w
	FROM   	SL_UNID_ATEND
	WHERE 	nr_sequencia = nr_sequencia_w;

ELSIF (ie_opcao_p = 'E') THEN
	SELECT 	MAX(NR_SEQ_SERVICO_EXEC)
	INTO STRICT	ds_retorno_w
	FROM   	SL_UNID_ATEND
	WHERE 	nr_sequencia = nr_sequencia_w;

ELSIF (ie_opcao_p = 'N') THEN
	ds_retorno_w	:= nr_sequencia_w;
END IF;

RETURN	ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
