SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_iniciais_descricao ( ds_descricao_p text) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w		varchar(255);
ds_descricao_w		varchar(255);
ds_descricao_ww		varchar(255);

qt_nome_w		bigint := 1;
qt_xx_w			bigint := 1;
x			varchar(255);

BEGIN 
 
for i in 1..length(ds_descricao_p || ' ') loop 
		x	:= substr(ds_descricao_p || ' ', i, 1);
		if (x = ' ') then 
			qt_nome_w := qt_nome_w + 1;
		end if;
	end loop;
 
	while qt_xx_w <= qt_nome_w loop 
 
		ds_descricao_w := OBTER_VALOR_CAMPO_SEPARADOR(ds_descricao_p,qt_xx_w,' ');
		if (qt_xx_w <> qt_nome_w) then 
			if (Upper(ds_descricao_w) not in ('DE', 'A', 'E', 'I', 'O', 'U','UM', 'UMA', 'DO', 'PARA', 'POR', 'DA', 'AO', 'ENTRE', 'COM', 'QUE', 'COMO')) then 
			  ds_descricao_ww := ds_descricao_ww || substr(ds_descricao_w,1,1);
			end if;
		end if;
		qt_xx_w := qt_xx_w + 1;
 
	end loop;
 
ds_retorno_w := Upper(ds_descricao_ww);
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
