CREATE OR REPLACE FUNCTION obter_iniciais_nome_senhas ( cd_pessoa_fisica_p text, nm_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(20);
nm_pessoa_fisica_w	varchar(255);
nm_social_pf_w		varchar(255);
nm_utilizado_w		varchar(255);


BEGIN

nm_pessoa_fisica_w := nm_pessoa_fisica_p;

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	select	SUBSTR(MAX(OBTER_NOME_PF(CD_PESSOA_FISICA)),1,255),
		max(nm_social)
	into STRICT	nm_pessoa_fisica_w,
		nm_social_pf_w
	from	pessoa_fisica
	where	cd_pessoa_fisica = cd_pessoa_fisica_p;
end if;

if (coalesce(nm_social_pf_w::text, '') = '') then
	nm_utilizado_w := nm_pessoa_fisica_w;
else
	nm_utilizado_w := nm_social_pf_w;
end if;

ds_retorno_w := substr(nm_utilizado_w,1,1);

while length(nm_utilizado_w) > 0 loop
	if (substr(nm_utilizado_w,1,1) = ' ') then
		ds_retorno_w := ds_retorno_w || substr(nm_utilizado_w,2,1);
	end if;
	
	nm_utilizado_w := substr(nm_utilizado_w,2,length(nm_utilizado_w));
	
end loop;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
