CREATE OR REPLACE FUNCTION obter_iniciais_nome ( cd_pessoa_fisica_p text, nm_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(20);
nm_pessoa_fisica_w	varchar(255);


BEGIN

nm_pessoa_fisica_w := nm_pessoa_fisica_p;

if (coalesce(nm_pessoa_fisica_w::text, '') = '') and (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	select	SUBSTR(OBTER_NOME_PF(CD_PESSOA_FISICA),0,255)
	into STRICT	nm_pessoa_fisica_w
	from	pessoa_fisica
	where	cd_pessoa_fisica = cd_pessoa_fisica_p;
end if;

ds_retorno_w := substr(nm_pessoa_fisica_w,1,1);

while length(nm_pessoa_fisica_w) > 0 loop
	if (substr(nm_pessoa_fisica_w,1,1) = ' ') then
		ds_retorno_w := substr(ds_retorno_w || substr(nm_pessoa_fisica_w,2,1),1,20);
	end if;
	nm_pessoa_fisica_w := substr(nm_pessoa_fisica_w,2,length(nm_pessoa_fisica_w));
end loop;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
