CREATE OR REPLACE FUNCTION obter_inicio_fim_semana ( dt_referencia_p timestamp, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(100);
ie_dia_sem_w		varchar(10);
/* I - Para obter a data de inicio da semana */

/* F - Para obter a data de fim da semana    */

BEGIN
ie_dia_sem_w	:= obter_cod_dia_semana(to_date(dt_referencia_p));
/*if	(philips_param_pck.get_nr_seq_idioma = 2) then -- Espanhol  - MÃ©xico(MX)
	if	(ie_dia_sem_w = 7) then
		ie_dia_sem_w := 1;
	else
		ie_dia_sem_w	:= ie_dia_sem_w + 1;
	end if;
end if;*/
if (ie_opcao_p = 'I') then
	select (to_date(dt_referencia_p) - ie_dia_sem_w+1)
	into STRICT	ds_retorno_w
	;
elsif (ie_opcao_p = 'F') then
	select (to_date(dt_referencia_p) - ie_dia_sem_w+7)
	into STRICT	ds_retorno_w
	;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
