CREATE OR REPLACE FUNCTION obter_inicio_prescr_sae ( cd_setor_atendimento_p bigint, dt_prescricao_p timestamp, dt_primeiro_horario_p timestamp) RETURNS timestamp AS $body$
DECLARE

dt_inicio_prescricao_w	timestamp;
dt_retorno_w		timestamp;



BEGIN
select	max(HR_INICIO_PRESCRICAO_SAE)
into STRICT	dt_inicio_prescricao_w
from	setor_atendimento
where	cd_setor_atendimento	= cd_setor_atendimento_p;

if (dt_primeiro_horario_p IS NOT NULL AND dt_primeiro_horario_p::text <> '') then
	
	if (to_char(dt_primeiro_horario_p,'hh24:mi') < to_char(dt_prescricao_p,'hh24:mi')) then
		dt_retorno_w :=  ESTABLISHMENT_TIMEZONE_UTILS.DATEATTIME(dt_prescricao_p+1,dt_primeiro_horario_p);
	else
		dt_retorno_w :=  ESTABLISHMENT_TIMEZONE_UTILS.DATEATTIME(dt_prescricao_p,dt_primeiro_horario_p);
	end if;
else
	dt_retorno_w	:= dt_prescricao_p;
end if;
return	dt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
