CREATE OR REPLACE FUNCTION obter_inicio_proc_cirurgia ( nr_cirurgia_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint) RETURNS timestamp AS $body$
DECLARE

nr_cirurgia_w			bigint;
cd_procedimento_w		bigint;
ie_origem_proced_w		bigint;
dt_inicio_cirurgia_w		timestamp;
dt_inicio_procedimento_w	timestamp;

BEGIN

if (nr_cirurgia_p IS NOT NULL AND nr_cirurgia_p::text <> '') then
	select	max(coalesce(dt_inicio_real,dt_inicio_prevista))
	into STRICT	dt_inicio_cirurgia_w
	from	cirurgia
	where	nr_cirurgia		= nr_cirurgia_p;
end if;

return	dt_inicio_cirurgia_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
