CREATE OR REPLACE FUNCTION obter_inicio_quimio_grid_gpt (nr_atendimento_p bigint, cd_pessoa_fisica_p text) RETURNS timestamp AS $body$
DECLARE

				
nr_sequencia_w			gpt_hist_analise_plano.nr_sequencia%type;
dt_inicio_analise_w 	gpt_hist_analise_plano.dt_inicio_analise%type;
dt_fim_analise_w		gpt_hist_analise_plano.dt_fim_analise%type;
dt_prescricao_w			timestamp;

BEGIN

select	max(nr_sequencia)
into STRICT	nr_sequencia_w
from	gpt_hist_analise_plano
where	((nr_atendimento = nr_atendimento_p) or (cd_pessoa_fisica = cd_pessoa_fisica_p and coalesce(nr_atendimento::text, '') = '' and coalesce(nr_atendimento_p,0) = 0))
and		ie_tipo_usuario = 'E';

if (nr_sequencia_w IS NOT NULL AND nr_sequencia_w::text <> '') and (nr_sequencia_w > 0) then
	select 	max(dt_inicio_analise),
			max(dt_fim_analise)
	into STRICT	dt_inicio_analise_w,
			dt_fim_analise_w
	from 	gpt_hist_analise_plano
	where	nr_sequencia = nr_sequencia_w;
end if;

if (dt_fim_analise_w IS NOT NULL AND dt_fim_analise_w::text <> '') then
	dt_prescricao_w := dt_fim_analise_w;
elsif (dt_inicio_analise_w IS NOT NULL AND dt_inicio_analise_w::text <> '') then
	dt_prescricao_w := dt_inicio_analise_w;
else
	dt_prescricao_w := trunc(clock_timestamp(),'dd') - 2;
end if;

return dt_prescricao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
