CREATE OR REPLACE FUNCTION obter_inss_titulo ( nr_titulo_p bigint) RETURNS bigint AS $body$
DECLARE


vl_inss_w		double precision;
cd_tributo_w		integer;


BEGIN

select	coalesce(vl_parametro, vl_parametro_padrao)
into STRICT	cd_tributo_w
from	funcao_parametro
where	cd_funcao	= 851
and	nr_sequencia	= 4;

select max(vl_imposto)
into STRICT	vl_inss_w
from titulo_pagar_imposto
where	nr_titulo	= nr_titulo_p
  and	cd_tributo	= cd_tributo_w
  and	ie_pago_prev	= 'V';

Return vl_inss_w;

END;
