		rpad(upper(elimina_acentuacao(CASE WHEN coalesce(nr_seq_mensalidade_w::text, '') = '' THEN  obter_dados_pf_pj(cd_pessoa_fisica_w, cd_cgc_w, 'E')  ELSE pls_obter_compl_pagador(nr_seq_pagador_w,'CI') END )),15,' ') || ' ' ||
