			((ie_condicao_w = 'I' AND ds_tipo_taxa_multa_w <> ds_valor_regra_w) or
			((ie_condicao_w = 'D') and
			(((coalesce(ds_valor_regra_w::text, '') = '') and (coalesce(ds_tipo_taxa_multa_w::text, '') = '')) or (ds_tipo_taxa_multa_w = ds_valor_regra_w)))) then

			ds_instrucao_w	:= null;

