CREATE OR REPLACE FUNCTION obter_instrucao_titulo_receber (nr_titulo_p bigint) RETURNS varchar AS $body$
DECLARE


cd_estabelecimento_w		smallint;
ds_instrucao_w			varchar(4000);
vl_titulo_w			double precision;
vl_juro_w			double precision;
pr_juro_w			double precision 	:= 3;
cd_moeda_w			titulo_receber.cd_moeda%TYPE;


BEGIN

SELECT coalesce(vl_titulo,0),
	coalesce(tx_juros,0),
	cd_estabelecimento,
	cd_moeda
INTO STRICT	vl_titulo_w,
	pr_juro_w,
	cd_estabelecimento_w,
	cd_moeda_w
FROM	titulo_receber
WHERE	nr_titulo	= nr_titulo_p;

BEGIN

SELECT ds_instrucao_bloqueto
INTO STRICT ds_instrucao_w
FROM parametro_contas_receber
WHERE cd_estabelecimento	= cd_estabelecimento_w;

EXCEPTION
WHEN OTHERS THEN
	ds_instrucao_w	:= '';
END;

vl_juro_w	:= vl_titulo_w * pr_juro_w / 100 / 30;
IF (pr_juro_w > 0) THEN
	ds_instrucao_w	:=	ds_instrucao_w || CHR(13) || CHR(10) ||
				wheb_mensagem_pck.get_texto(301941) || ' ' || obter_desc_sigla_moeda(cd_moeda_w) || ' ' || Campo_Mascara_casas(vl_juro_w,2);
END IF;

RETURN ds_instrucao_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
