SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_integra_associado (nr_seq_interno_p bigint) RETURNS varchar AS $body$
DECLARE


ie_associado_w		varchar(1);
retorno_w		 varchar(10);	
nr_seq_proc_interno_w bigint;
ie_n_envia_associado_w varchar(1);


BEGIN

if (nr_seq_interno_p IS NOT NULL AND nr_seq_interno_p::text <> '') then
	begin

select ( coalesce(a.IE_EXAME_ASSOC,'N')),
       (a.NR_SEQ_PROC_INTERNO)
  into STRICT ie_associado_w,
        nr_seq_proc_interno_w
  from prescr_procedimento a
 where a.NR_SEQ_INTERNO = nr_seq_interno_p;

 
 
 if ((ie_associado_w = 'S') or (ie_associado_w = 'A')) then
 begin
   select max(coalesce(IE_N_ENVIA_ASSOCIADO,'N'))
   into STRICT ie_n_envia_associado_w
   from PROC_INTERNO_INTEGRACAO
   where NR_SEQ_PROC_INTERNO = nr_seq_proc_interno_w;
   
 end;
 else
 ie_n_envia_associado_w := 'N';
 end if;
end;
end if;

if (coalesce(ie_n_envia_associado_w::text, '') = '') then 
  retorno_w := 'N';
 else
  retorno_w := ie_n_envia_associado_w;
end if;
 return retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
