CREATE OR REPLACE FUNCTION obter_intervalos_sugeridos ( cd_material_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
ds_intervalo_w		varchar(255);

c01 CURSOR FOR
SELECT	coalesce(b.ds_prescricao, b.ds_intervalo)
from	rep_intervalo_sugerido a,
		intervalo_prescricao b
where	cd_material = cd_material_p
and		a.cd_intervalo = b.cd_intervalo;


BEGIN

open C01;
loop
fetch C01 into
	ds_intervalo_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	if (coalesce(ds_retorno_w::text, '') = '') then
		ds_retorno_w	:= substr(ds_intervalo_w,1,255);
	else
		ds_retorno_w	:= substr(ds_retorno_w || ', ' || ds_intervalo_w,1,255);
	end if;
end loop;
close C01;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
