CREATE OR REPLACE FUNCTION obter_intervalo_dieta (nr_prescricao_p bigint, cd_refeicao_p text) RETURNS varchar AS $body$
DECLARE


ds_interv_w	varchar(255);


BEGIN

select	max(Substr(Obter_Unid_Med_Dieta(b.cd_dieta),1,150))
into STRICT	ds_interv_w
from	horario_refeicao_dieta a,
	prescr_dieta b,
	prescr_medica k
where	((b.cd_dieta = a.cd_dieta) or ((coalesce(a.cd_dieta::text, '') = '') and (obter_se_dieta_hor_excl_ref(b.cd_dieta,a.cd_refeicao) = 'N')))
and	k.nr_prescricao = nr_prescricao_p
and	k.nr_prescricao	= b.nr_prescricao
and	coalesce(k.dt_suspensao::text, '') = ''
and	a.cd_refeicao	= cd_refeicao_p
and	coalesce(b.ie_suspenso,'N') <> 'S';

return	ds_interv_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
