CREATE OR REPLACE FUNCTION obter_intervalo_glicemia (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(80);
cd_intervalo_w		varchar(10);
nr_prescricao_w		bigint;
nr_sequencia_w          bigint;



BEGIN

select nr_prescricao,
       nr_seq_procedimento
into STRICT   nr_prescricao_w,
       nr_sequencia_w
from   atend_glicemia
where  nr_sequencia = nr_sequencia_p;

select coalesce(max(cd_intervalo),'')
into STRICT   cd_intervalo_w
from   prescr_procedimento
where  nr_sequencia  = nr_sequencia_w
and    nr_prescricao = nr_prescricao_w;

ds_retorno_w := substr(OBTER_DESC_INTERVALO(cd_intervalo_w),1,80);

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
