CREATE OR REPLACE FUNCTION obter_intervalo_med_rot ( ie_opcao_p text, cd_intervalo_p text) RETURNS varchar AS $body$
DECLARE


cd_intervalo_w		varchar(10);


BEGIN
if (ie_opcao_p = 'ACM') then
	select  max(cd_intervalo)
	into STRICT	cd_intervalo_w
	from 	intervalo_prescricao
    where   ie_acm = 'S'
    and     ie_situacao = 'A'
	and     cd_intervalo = cd_intervalo_p
	and     Obter_se_intervalo(cd_intervalo, 1) = 'S';

	if (coalesce(cd_intervalo_w::text, '') = '') then
		select  max(cd_intervalo)
		into STRICT	cd_intervalo_w
		from 	intervalo_prescricao
		where   ie_acm = 'S'
		and     ie_situacao = 'A'
		and     Obter_se_intervalo(cd_intervalo, 1) = 'S';
	end if;
elsif (ie_opcao_p = 'SN') then
	select  max(cd_intervalo)
	into STRICT	cd_intervalo_w
	from 	intervalo_prescricao
    where   ie_se_necessario = 'S'
    and     ie_situacao = 'A'
	and     cd_intervalo = cd_intervalo_p
	and     Obter_se_intervalo(cd_intervalo, 1) = 'S';

	if (coalesce(cd_intervalo_w::text, '') = '') then
		select  max(cd_intervalo)
		into STRICT	cd_intervalo_w
		from 	intervalo_prescricao
		where   ie_se_necessario = 'S'
		and     ie_situacao = 'A'
		and     Obter_se_intervalo(cd_intervalo, 1) = 'S';
	end if;
end if;

return	cd_intervalo_w;

end;
