SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_intervalo_prescr_audit ( nr_sequencia_p bigint, ie_proc_mat_p bigint) RETURNS varchar AS $body$
DECLARE


nr_prescricao_w			bigint;
nr_sequencia_prescricao_w		integer;
ds_retorno_w			varchar(80) := '';

BEGIN

if (ie_proc_mat_p = 1) then

	select 	max(nr_prescricao),
		max(nr_sequencia_prescricao)
	into STRICT	nr_prescricao_w,
		nr_sequencia_prescricao_w
	from	procedimento_paciente
	where	nr_sequencia = nr_sequencia_p;

	select	max(obter_desc_intervalo(cd_intervalo))
	into STRICT	ds_retorno_w
	from	prescr_procedimento
	where	nr_prescricao = nr_prescricao_w
	and	nr_sequencia = nr_sequencia_prescricao_w;

else

	select 	max(nr_prescricao),
		max(nr_sequencia_prescricao)
	into STRICT	nr_prescricao_w,
		nr_sequencia_prescricao_w
	from	material_atend_paciente
	where	nr_sequencia = nr_sequencia_p;

	select	max(obter_desc_intervalo(cd_intervalo))
	into STRICT	ds_retorno_w
	from	prescr_material
	where	nr_prescricao = nr_prescricao_w
	and	nr_sequencia = nr_sequencia_prescricao_w;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
