CREATE OR REPLACE FUNCTION obter_intervalo_procedimento ( cd_procedimento_p text, ie_origem_proced_p bigint, nr_seq_proc_interno_p bigint, cd_setor_prescr_p bigint) RETURNS varchar AS $body$
DECLARE


cd_intervalo_w	varchar(7);


BEGIN

select	max(cd_intervalo)
into STRICT	cd_intervalo_w
from	procedimento
where	cd_procedimento		=	cd_procedimento_p
and	ie_origem_proced	=	ie_origem_proced_p;

if (coalesce(cd_intervalo_w::text, '') = '') then
	select	max(cd_intervalo)
	into STRICT	cd_intervalo_w
	from	rep_intervalo_proced
	where	coalesce(cd_procedimento,cd_procedimento_p) = cd_procedimento_p
	and	((coalesce(cd_procedimento::text, '') = '') or (ie_origem_proced = ie_origem_proced_p))
	and	((coalesce(nr_seq_proc_interno::text, '') = '') or (nr_seq_proc_interno 		= nr_seq_proc_interno_p))
	and	((coalesce(cd_setor_atendimento::text, '') = '') or (cd_setor_atendimento	= cd_setor_prescr_p));
end if;

return	cd_intervalo_w;

end;
$body$
LANGUAGE PLPGSQL
