SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_interv_cuidados_hm ( nr_prescricao_p bigint, nr_seq_item_p bigint, ie_consulta_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1000);


BEGIN

if (ie_consulta_p <> 'S') then
	ds_retorno_w := null;
else
	select	trim(both CASE WHEN ie_lavado='S' THEN obter_desc_expressao(292460)/*'Lavado -'*/  ELSE null END      ||
			CASE WHEN ie_irradiado='S' THEN obter_desc_expressao(292247)/*' Irradiado -'*/  ELSE null END    ||
			CASE WHEN ie_aliquotado='S' THEN obter_desc_expressao(283367)/*' Aliquotado -'*/  ELSE null END  ||
			CASE WHEN ie_filtrado='S' THEN obter_desc_expressao(290096)/*' Filtrado -'*/  ELSE null END )
	into STRICT	ds_retorno_w
	from	san_reserva_item
	where	nr_prescricao = nr_prescricao_p
	and		nr_seq_item = nr_seq_item_p
	and		(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '');
end if;

if (coalesce(ds_retorno_w::text, '') = '') then
	return null;
else
	return	'('||substr(ds_retorno_w,1,length(ds_retorno_w)-2)||')';
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
