SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_ipi_rateio_item_nf ( nr_sequencia_p bigint, nr_item_nf_p bigint) RETURNS bigint AS $body$
DECLARE


vl_retorno_w			double precision;
qt_item_total_nf_w			double precision;
qt_item_nf_w			double precision;
vl_ipi_w				double precision;
vl_porcento_w			double precision;


BEGIN

select	coalesce(vl_ipi,0)
into STRICT	vl_ipi_w
from	nota_fiscal
where	nr_sequencia = nr_sequencia_p;

select	sum(qt_item_nf)
into STRICT	qt_item_total_nf_w
from	nota_fiscal_item
where	nr_sequencia = nr_sequencia_p;

select	sum(qt_item_nf)
into STRICT	qt_item_nf_w
from	nota_fiscal_item
where	nr_sequencia = nr_sequencia_p
and	nr_item_nf = nr_item_nf_p;

vl_porcento_w	:= dividir((qt_item_nf_w * 100),qt_item_total_nf_w); /* decobrir quantos % o item representa na nota*/
vl_retorno_w	:= coalesce(dividir((vl_ipi_w * vl_porcento_w),100),0);


return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
