CREATE OR REPLACE FUNCTION obter_item_consig_prescr_mat ( cd_material_p bigint, nr_prescricao_p bigint, nr_sequencia_p bigint) RETURNS bigint AS $body$
DECLARE

	w_ie_consignado	integer := 0;

BEGIN
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') and (cd_material_p IS NOT NULL AND cd_material_p::text <> '') then
	begin
	select	b.ie_consignado
	into STRICT 	w_ie_consignado
	from  	material b,
		prescr_material  a
	where 	a.cd_material    = b.cd_material
	and 	a.nr_prescricao  = nr_prescricao_p
	and 	a.cd_material	 = cd_material_p
	and	a.nr_sequencia   = nr_sequencia_p
	and 	coalesce(a.ie_medicacao_paciente,'N') <> 'S'
	and 	coalesce(a.ie_suspenso,'N') <> 'S';
	end;
end if;

return	w_ie_consignado;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
