CREATE OR REPLACE FUNCTION obter_item_desc_ageint ( nr_Seq_proc_interno_p bigint, cd_medico_p text, cd_Especialidade_p bigint) RETURNS varchar AS $body$
DECLARE

 
ds_procedimento_w	varchar(100);
nm_medico_w		varchar(60);
ds_especialidade_w	varchar(255);
ds_retorno_w		varchar(500);
			

BEGIN 
 
select	max(coalesce(ds_exame_curto, ds_proc_exame)) 
into STRICT	ds_procedimento_w 
from	proc_interno 
where	nr_sequencia	= nr_seq_proc_interno_p;
 
select	max(SUBSTR(OBTER_NOME_PF(CD_PESSOA_FISICA),0,60)) 
into STRICT	nm_medico_W 
from	pessoa_fisica 
where	cd_pessoa_fisica	= cd_medico_p;
 
select 	max(ds_especialidade) 
into STRICT 	ds_especialidade_w 
from 	especialidade_medica 
where 	cd_especialidade = cd_especialidade_p;
 
if (ds_procedimento_w IS NOT NULL AND ds_procedimento_w::text <> '') then 
	ds_retorno_w	:= ds_procedimento_w || ' ';
end if;
if (ds_especialidade_w IS NOT NULL AND ds_especialidade_w::text <> '') then 
	ds_retorno_w	:= ds_retorno_w || ' ' || ds_especialidade_w || ' ';
end if;
if (nm_medico_w IS NOT NULL AND nm_medico_w::text <> '') then 
	ds_retorno_w	:= ds_retorno_w || ' ' || nm_medico_w;
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
