CREATE OR REPLACE FUNCTION obter_item_grid_ageint ( nr_Seq_proc_interno_p bigint, cd_medico_p text, cd_Especialidade_p bigint, cd_estabelecimento_p bigint, nr_seq_item_p bigint default null, ie_rodizio_especialidade_p text default 'N') RETURNS varchar AS $body$
DECLARE


ds_procedimento_w	varchar(100);
nm_medico_w		varchar(60);
ds_especialidade_w	varchar(255);
ds_retorno_w		varchar(500);
ds_proc_exame_w		varchar(500);
ie_desc_curta_proc_int_w	varchar(1);
			

BEGIN

select	coalesce(max(ie_mostra_desc_curto_proced),'N')
into STRICT	ie_desc_curta_proc_int_w
from	parametro_agenda_integrada
where	coalesce(cd_estabelecimento, cd_estabelecimento_p) = cd_estabelecimento_p;

if (coalesce(nr_Seq_proc_interno_p,0) > 0) then
	begin
	
	select	max(ds_proc_exame)
	into STRICT	ds_proc_exame_w
	from	proc_interno
	where	nr_sequencia	= nr_seq_proc_interno_p;
	
	if (ie_desc_curta_proc_int_w = 'N') then
		select	max(ds_proc_exame)
		into STRICT	ds_retorno_w
		from	proc_interno
		where	nr_sequencia	= nr_seq_proc_interno_p;
	elsif (ie_desc_curta_proc_int_w = 'P') then
		select	coalesce(max(ds_exame_curto),ds_proc_exame_w)
		into STRICT	ds_retorno_w
		from	proc_interno
		where	nr_sequencia	= nr_seq_proc_interno_p;
	elsif (ie_desc_curta_proc_int_w = 'G') then
		select	coalesce(max(nm_curto),ds_proc_exame_w)
		into STRICT	ds_retorno_w
		from	agenda_int_grupo_item
		where	nr_sequencia	= nr_seq_item_p;
	end if;	
	
	end;
end if;

if (coalesce(cd_especialidade_p,0) > 0) then
	begin
	select 	max(ds_especialidade)
	into STRICT 	ds_retorno_w
	from 	especialidade_medica
	where 	cd_especialidade = cd_especialidade_p;
	
	if (ie_desc_curta_proc_int_w = 'G') then
		select	coalesce(max(nm_curto),ds_retorno_w)
		into STRICT	ds_retorno_w
		from	agenda_int_grupo_item
		where	nr_sequencia	= nr_seq_item_p;
	end if;	
	
	end;
end if;

if	((coalesce(cd_medico_p,'ZZZ') <> 'ZZZ') and coalesce(ie_rodizio_especialidade_p,'N') = 'N') then
	begin	
	select	substr(CASE WHEN coalesce(ds_retorno_w,'')='' THEN max(OBTER_NOME_PF(CD_PESSOA_FISICA))  ELSE ds_retorno_w||' - '||max(OBTER_NOME_PF(CD_PESSOA_FISICA)) END ,0,50)
	into STRICT	ds_retorno_w
	from	pessoa_fisica
	where	cd_pessoa_fisica	= cd_medico_p;
	
	if (ie_desc_curta_proc_int_w = 'G') then
		select	coalesce(max(nm_curto),ds_retorno_w)
		into STRICT	ds_retorno_w
		from	agenda_int_grupo_item
		where	nr_sequencia	= nr_seq_item_p;
	end if;	
	
	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_item_grid_ageint ( nr_Seq_proc_interno_p bigint, cd_medico_p text, cd_Especialidade_p bigint, cd_estabelecimento_p bigint, nr_seq_item_p bigint default null, ie_rodizio_especialidade_p text default 'N') FROM PUBLIC;

