CREATE OR REPLACE FUNCTION obter_item_index ( nr_seq_item_p bigint, nr_index_p bigint) RETURNS bigint AS $body$
DECLARE

nr_seq_item_result_w	bigint;
nr_index_w				smallint;

c01 CURSOR FOR
SELECT	nr_sequencia,
		row_number() OVER () - 1
from (
		SELECT	nr_sequencia
		from	srpa_item_result
		where	nr_seq_item = nr_seq_item_p
		order by nr_sequencia) alias0;


BEGIN

if (nr_index_p <> -1) then
	begin
	open c01;
	loop
	fetch c01 into
		nr_seq_item_result_w,
		nr_index_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		if (nr_index_w = nr_index_p) then
			begin
			return nr_seq_item_result_w;
			exit;
			end;
		end if;
		end;
	end loop;
	close c01;
	end;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
