SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_item_prescr_opme ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE



ds_result_w		varchar(500);
ie_agrupador_w		smallint;
ds_procedimento_w	varchar(400);

expressao1_w	varchar(255) := obter_desc_expressao_idioma(314495, null, wheb_usuario_pck.get_nr_seq_idioma);--Cadeira de rodas
BEGIN

SELECT	max(ie_agrupador),
	max(SUBSTR(OBTER_DESCRICAO_PROCEDIMENTO(cd_procedimento,ie_origem_proced),1,255))
into STRICT	ie_agrupador_w,
	ds_procedimento_w
FROM	prescr_opme
WHERE	nr_sequencia	= nr_sequencia_p
AND	coalesce(ie_suspenso, 'N')	<> 'S';


IF (ie_agrupador_w = 1 ) THEN
	Ds_result_w	:= Ds_result_w ||expressao1_w || ' '|| ' - ' || ds_procedimento_w;
END IF;


RETURN Ds_result_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
