SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_item_princ_agrupamento ( nr_prescricao_p bigint, nr_agrupamento_p bigint) RETURNS bigint AS $body$
DECLARE


nr_sequencia_princ_w	bigint;


BEGIN

select	min(nr_sequencia)
into STRICT	nr_sequencia_princ_w
from	prescr_material
where	ie_agrupador	= 1
and	nr_agrupamento	= nr_agrupamento_p
and	ie_item_superior = 'S'
and	nr_prescricao	= nr_prescricao_p;

if (coalesce(nr_sequencia_princ_w,0) = 0) then
	select	min(nr_sequencia)
	into STRICT	nr_sequencia_princ_w
	from	prescr_material
	where	ie_agrupador	= 1
	and	nr_agrupamento	= nr_agrupamento_p
	and	nr_prescricao	= nr_prescricao_p;
end if;

return	nr_sequencia_princ_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
