SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_item_princ_disp ( nr_prescricao_p bigint, nr_seq_prescr_sol_p bigint, nr_seq_prescr_mat_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);
ds_componente_w	varchar(255);
ds_tamanho_limite_w	smallint := 80;

C01 CURSOR FOR
	SELECT 	substr(Obter_Descricao_mat_prescr(cd_material), 1, ds_tamanho_limite_w) DS_MATERIAL
	from   	prescr_material a
	where  	nr_prescricao =	nr_prescricao_p
	and    	ie_agrupador in (4,5)
	and	nr_sequencia_solucao = nr_seq_prescr_sol_p
	order by nr_agrupamento,
		nr_sequencia;


BEGIN
if (nr_seq_prescr_sol_p IS NOT NULL AND nr_seq_prescr_sol_p::text <> '')   then
	open c01;
	loop
    	fetch c01 into
        	ds_componente_w;
        EXIT WHEN NOT FOUND or length(ds_retorno_w) >= ds_tamanho_limite_w;  /* apply on c01 */

        if (length(ds_retorno_w) > 1)    then
                ds_retorno_w	:=	ds_retorno_w || ' - ' ||ds_componente_w;
            else
                ds_retorno_w    := ds_componente_w;
        end if;

	end loop;
	close C01;

elsif (nr_seq_prescr_mat_p IS NOT NULL AND nr_seq_prescr_mat_p::text <> '')   then
	select	substr(max(VIPE_obter_se_medic_composto(nr_prescricao_p, x.nr_sequencia, x.nr_agrupamento, 'D')), 1, ds_tamanho_limite_w)
	into STRICT	ds_retorno_w
	from	prescr_material x
	where	nr_prescricao	= nr_prescricao_p
	and nr_sequencia	= nr_seq_prescr_mat_p;

end if;

if (length(ds_retorno_w) >= ds_tamanho_limite_w )  then
        ds_retorno_w    :=	substr(trim(both ds_retorno_w), 1, (ds_tamanho_limite_w - 3)) || '...';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
