CREATE OR REPLACE FUNCTION obter_item_pront_lib ( IE_NIVEL_ATENCAO_DEST_P text, NR_SEQ_ITEM_PRONT_P bigint) RETURNS varchar AS $body$
DECLARE


retorno_w	varchar(1) := 'S';


BEGIN

if ( ie_nivel_atencao_dest_p = 'P') then

	SELECT	coalesce(MAX('S'),'N')
	into STRICT	retorno_w
	FROM	prontuario_item
	WHERE   nr_sequencia = nr_seq_item_pront_p
	AND		IE_EXIBIR_PC = 'S';

elsif ( ie_nivel_atencao_dest_p = 'S') then

	SELECT	coalesce(MAX('S'),'N')
	into STRICT	retorno_w
	FROM	prontuario_item
	WHERE   nr_sequencia = nr_seq_item_pront_p
	AND		IE_EXIBIR_SC = 'S';

end if;

return	retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
