CREATE OR REPLACE FUNCTION obter_item_pront_pep ( nr_sequencia_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(100) := '';


BEGIN
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	select	substr(coalesce(ds_item_instituicao, obter_desc_expressao(cd_exp_desc_item, ds_item)),1,100)
	into STRICT	ds_retorno_w
	from	prontuario_item
	where	nr_sequencia	= nr_sequencia_p;

end if;
return ds_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
