CREATE OR REPLACE FUNCTION obter_item_servico_item_nf ( cd_material_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*ie_opcao_p
S - NR_SEQUENCIA
C - CD_ITEM_SERVICO
D -DS_ITEM_SERVICO
P - CD_PREFEITURA
*/
			
			
qt_total_item_w		bigint;
ds_retorno_w		varchar(500);
nr_seq_item_serv_w	bigint;
nr_sequencia_w		grupo_servico_item.nr_sequencia%type;
cd_item_servico_w	grupo_servico_item.cd_item_servico%type;
ds_item_servico_w	grupo_servico_item.ds_item_servico%type;
cd_prefeitura_w		grupo_servico_item.cd_prefeitura%type;


BEGIN

if (cd_material_p > 0) then
	select	max(a.nr_seq_item_serv)
	into STRICT	nr_seq_item_serv_w
	from	material_fiscal a
	where	a.cd_material	= cd_material_p;
elsif (cd_procedimento_p > 0) then
	select	max(a.nr_seq_item_serv)
	into STRICT	nr_seq_item_serv_w
	from	procedimento a
	where	a.cd_procedimento	= cd_procedimento_p
	and	a.ie_origem_proced	= ie_origem_proced_p;
end if;

if (nr_seq_item_serv_w > 0) then
	
	select	nr_sequencia,
		cd_item_servico,
		ds_item_servico,
		cd_prefeitura
	into STRICT	nr_sequencia_w,
		cd_item_servico_w,
		ds_item_servico_w,
		cd_prefeitura_w
	from	grupo_servico_item
	where	nr_sequencia = nr_seq_item_serv_w;
end if;

if (ie_opcao_p = 'S') then
	ds_retorno_w := nr_sequencia_w;
elsif (ie_opcao_p = 'C') then
	ds_retorno_w := cd_item_servico_w;
elsif (ie_opcao_p = 'D') then
	ds_retorno_w := ds_item_servico_w;
elsif (ie_opcao_p = 'P') then
	ds_retorno_w := cd_prefeitura_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
