CREATE OR REPLACE FUNCTION obter_item_servico_proced ( cd_procedimento_p bigint, ie_origem_proced_p bigint, cd_estabelecimento_p bigint default 0) RETURNS bigint AS $body$
DECLARE


/*Lcp 116/2003*/

nr_seq_item_serv_w	bigint;
nr_seq_retorno_w		bigint;


BEGIN
select	max(coalesce(nr_seq_item_serv, 0))
into STRICT	nr_seq_item_serv_w
from	procedimento
where	cd_procedimento = cd_procedimento_p
and	ie_origem_proced = ie_origem_proced_p;


if (cd_estabelecimento_p <> 0) then
	begin
	select	max(nr_seq_servico_item)
	into STRICT	nr_seq_retorno_w
	from	procedimento_item_servico
	where	cd_procedimento = cd_procedimento_p
	and	ie_origem_proced = ie_origem_proced_p
	and	cd_estabelecimento = cd_estabelecimento_p;
	end;
end if;

if (coalesce(nr_seq_retorno_w::text, '') = '') then
	nr_seq_retorno_w := nr_seq_item_serv_w;
end if;

return	nr_seq_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
