SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_item_serv_matproc_nf (nr_seq_nota_fiscal_p nota_fiscal.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE


cd_material_w		nota_fiscal_item.cd_material%type;
cd_procedimento_w	nota_fiscal_item.cd_procedimento%type;
ie_origem_proced_w	nota_fiscal_item.ie_origem_proced%type;
nr_seq_proc_interno_w	nota_fiscal_item.nr_seq_proc_interno%type;
cd_estabelecimento_w	nota_fiscal_item.cd_estabelecimento%type;
nr_seq_item_serv_w	procedimento.nr_seq_item_serv%type;
cd_pessoa_fisica_w	nota_fiscal.cd_pessoa_fisica%type;
cd_cgc_emitente_w	nota_fiscal.cd_cgc_emitente%type;
ie_tipo_nota_w		nota_fiscal.ie_tipo_nota%type;


BEGIN

select	max(b.cd_material),
	max(b.cd_procedimento),
	coalesce(max(b.ie_origem_proced),0),
	max(b.nr_seq_proc_interno),
	max(a.cd_estabelecimento),
	max(a.cd_pessoa_fisica),
	max(a.cd_cgc_emitente),
	max(a.ie_tipo_nota)
into STRICT	cd_material_w,
	cd_procedimento_w,
	ie_origem_proced_w,
	nr_seq_proc_interno_w,
	cd_estabelecimento_w,
	cd_pessoa_fisica_w,
	cd_cgc_emitente_w,
	ie_tipo_nota_w
from	nota_fiscal a,
	nota_fiscal_item b
where	a.nr_sequencia = b.nr_sequencia
and	a.nr_sequencia = nr_seq_nota_fiscal_p
order by
	b.cd_material,
	b.cd_procedimento;


if (cd_procedimento_w IS NOT NULL AND cd_procedimento_w::text <> '') then
	begin

	if (ie_origem_proced_w = 0) then

		select 	max(ie_origem_proced)
		into STRICT	ie_origem_proced_w
		from 	procedimento
		where	cd_procedimento	= cd_procedimento_w;

	end if;

	select	obter_item_servico_proced(cd_procedimento_w,ie_origem_proced_w,cd_estabelecimento_w)
	into STRICT	nr_seq_item_serv_w
	;


	end;
elsif (nr_seq_proc_interno_w IS NOT NULL AND nr_seq_proc_interno_w::text <> '') then
	begin

	select	cd_procedimento,
		ie_origem_proced
	into STRICT	cd_procedimento_w,
		ie_origem_proced_w
	from	proc_interno
	where	nr_sequencia = nr_seq_proc_interno_w;

	select	obter_item_servico_proced(cd_procedimento_w,ie_origem_proced_w,cd_estabelecimento_w)
	into STRICT	nr_seq_item_serv_w
	;

	end;
else
	begin

	if (ie_tipo_nota_w = 'EF') then
		begin

		select	max(nr_seq_item_serv)
		into STRICT	nr_seq_item_serv_w
		from    pessoa_fis_conta_nf
		where	cd_pessoa_fisica = cd_pessoa_fisica_w;

		end;
	else
		begin

		select  max(nr_seq_item_serv)
		into STRICT	nr_seq_item_serv_w
		from    mat_lib_estrut_fornec
		where   cd_cnpj_fornec = cd_cgc_emitente_w
		and     cd_estabelecimento = cd_estabelecimento_w;

		end;
	end if;

	end;
end if;


return	nr_seq_item_serv_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
