SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_itens_assoc_hor_adep (ie_tipo_item_p text, nr_seq_horario_p bigint) RETURNS varchar AS $body$
DECLARE


ds_itens_assoc_w		varchar(2000);
ds_itens_composto_w		varchar(2000);


BEGIN
if (ie_tipo_item_p IS NOT NULL AND ie_tipo_item_p::text <> '') and (nr_seq_horario_p IS NOT NULL AND nr_seq_horario_p::text <> '') then
	if (ie_tipo_item_p in ('M', 'IAH')) then
	
		select	substr(adep_obter_medic_comp(max(a.nr_prescricao), max(a.nr_seq_material),nr_seq_horario_p),1,2000)
		into STRICT	ds_itens_composto_w
		from	prescr_mat_hor a
		where	nr_sequencia = nr_seq_horario_p;
		
		/*		
		select	substr(obter_diluicao_medic_adep(nr_seq_horario_p),1, 1000)
		into	ds_itens_assoc_w
		from	dual;
		*/
						
		return	ds_itens_composto_w;

	elsif (ie_tipo_item_p = 'P') then
		select	obter_mat_med_assoc_proc_adep(nr_seq_horario_p)
		into STRICT	ds_itens_assoc_w
		;

	elsif (ie_tipo_item_p = 'E') then
		select	obter_mat_med_assoc_sae_adep(nr_seq_horario_p)
		into STRICT	ds_itens_assoc_w
		;

	end if;

end if;

return ds_itens_assoc_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
