CREATE OR REPLACE FUNCTION obter_itens_assoc_interv (nr_seq_intervencao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_associados_w		varchar(254);
ds_retorno_w		varchar(4000);

c01 CURSOR FOR
	SELECT	substr(obter_desc_material(cd_material),1,255)
	into STRICT	ds_associados_w
	from   	pe_material_proced
	where  	nr_seq_proc = nr_seq_intervencao_p  --2212
	order by 1;


BEGIN

open c01;
loop
fetch c01 into
	ds_associados_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	if (ds_retorno_w IS NOT NULL AND ds_retorno_w::text <> '') then
		ds_retorno_w	:= ds_retorno_w||' - '||ds_associados_w;
	else
		ds_retorno_w	:= ds_associados_w;
	end if;
	end;
end loop;
close c01;


return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
