CREATE OR REPLACE FUNCTION obter_itens_concat_prod_opme (nr_lote_producao_p bigint, nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_itens_w	varchar(254);
ds_retorno	varchar(4000);

c01 CURSOR FOR
	SELECT	substr(obter_nm_documento_opme(a.nr_seq_documento),1,255)
	from	comp_documento_opme a
	where	1 = 1
	and  	a.nr_lote_producao  = nr_lote_producao_p
	and	a.nr_seq_componente = nr_sequencia_p
	order by 1;


BEGIN
open c01;
loop
fetch c01 into
	ds_itens_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	if coalesce(ds_retorno::text, '') = '' then
		ds_retorno := ds_itens_w;
	else
		ds_retorno := ds_retorno || ', ' || ds_itens_w;
	end if;
	end;
end loop;
close c01;

return	ds_retorno;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
