SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_itens_lote ( nr_prescricao_p bigint, nr_seq_lote_p bigint) RETURNS varchar AS $body$
DECLARE


qt_registro_w		bigint;
qt_registro_medic_w	bigint := 0;
qt_registro_mat_w	bigint := 0;
ds_result_w		varchar(500);
ie_agrupador_w		smallint;

C01 CURSOR FOR
	SELECT	count(*),
		b.ie_tipo_material
	from	prescr_mat_hor a,
		material b
	where	a.nr_prescricao	= nr_prescricao_p
	and	a.nr_seq_lote = nr_seq_lote_p
	and	a.cd_material = b.cd_material
	and	b.ie_tipo_material in (1,2,3)
	and	coalesce(a.dt_suspensao::text, '') = ''
	group by b.ie_tipo_material;


BEGIN

open c01;
loop
fetch c01 into
	qt_registro_w,
	ie_agrupador_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	if (ie_agrupador_w in (2,3)) then
		qt_registro_medic_w	:= qt_registro_w;
	elsif (ie_agrupador_w = 1) then
		qt_registro_mat_w	:= qt_registro_w;
	end if;

	end;
end loop;
close c01;

if (qt_registro_medic_w > 0) then
	ds_result_w	:= ds_result_w ||'  Medic '|| qt_registro_medic_w;
end if;

if (qt_registro_mat_w > 0) then
	ds_result_w	:= ds_result_w ||'  Mat '|| qt_registro_mat_w;
end if;

return	ds_result_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_itens_lote ( nr_prescricao_p bigint, nr_seq_lote_p bigint) FROM PUBLIC;

