CREATE OR REPLACE FUNCTION obter_itens_prescr_filtro (ds_filtro_p text, nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE


qt_registro_w		bigint;
Qt_registro_medic_w	bigint	:= 0;
Qt_registro_mat_w	bigint	:= 0;
Qt_registro_SNE_w	bigint	:= 0;
Qt_registro_supl_w	bigint	:= 0;
Qt_registro_assoc_proc_w	bigint	:= 0;
Ds_resultado_w	varchar(1);
ie_agrupador_w		smallint;
ds_retorno_w        varchar(1) := 'N';

C01 CURSOR FOR
	SELECT	count(*),
		ie_agrupador
	from	prescr_material
	where	nr_prescricao	= nr_prescricao_p
	and	ie_agrupador in (1,8,2,5)
	and	coalesce(ie_suspenso, 'N')	<> 'S'
	group by ie_agrupador;

  	function get_contido(ds_valor_p text ) return text is
      nr_res_w  smallint;
      ds_res_w  varchar(1) := 'N';

BEGIN
      if (ds_filtro_p IS NOT NULL AND ds_filtro_p::text <> '') then
        nr_res_w := position(ds_valor_p in ds_filtro_p);

        if (nr_res_w > 0) then
          ds_res_w := 'S';
        end if;
      end if;
      return ds_res_w;
    end;

BEGIN
open C01;
loop
fetch C01 into
	qt_registro_w,
	ie_agrupador_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (ie_agrupador_w = 1) then
		Qt_registro_medic_w	:= qt_registro_w;
	elsif (ie_agrupador_w = 8) then
		Qt_registro_SNE_w	:= qt_registro_w;
	elsif (ie_agrupador_w = 2) then
		Qt_registro_mat_w	:= qt_registro_w;
	elsif (ie_agrupador_w = 5) then
		Qt_registro_assoc_proc_w	:= qt_registro_w;
	end if;
	end;
end loop;
close C01;

if (Qt_registro_medic_w > 0) then
	ds_retorno_w := get_contido('1');
end if;

if	(ds_retorno_w = 'N' AND Qt_registro_SNE_w > 0) then
	ds_retorno_w := get_contido('3');
end if;

if	(ds_retorno_w = 'N' AND Qt_registro_mat_w > 0) then
	ds_retorno_w := get_contido('5');
end if;

if (ds_retorno_w = 'N' AND Qt_registro_assoc_proc_w > 0) then
	ds_retorno_w := get_contido('6');
end if;

if (ds_retorno_w = 'N') then
	select	count(*)
	into STRICT	Qt_registro_w
	from	prescr_solucao
	where	nr_prescricao	= nr_prescricao_p
	and	coalesce(ie_suspenso, 'N')	<> 'S'
	and	coalesce(ie_hemodialise,'N')	= 'N';

	if (Qt_registro_w <> 0) then
		ds_retorno_w := get_contido('4');
	end if;
end if;

if (ds_retorno_w = 'N') then
	select	count(*)
	into STRICT	qt_registro_w
	from	hd_prescricao
	where	nr_prescricao	= nr_prescricao_p;

	if (qt_registro_w <> 0) then

		select	count(*)
		into STRICT	qt_registro_w
		from	prescr_solucao
		where	nr_prescricao	= nr_prescricao_p
		and		coalesce(ie_suspenso, 'N')	<> 'S'
		and		coalesce(ie_hemodialise,'N')	= 'P';

		if (Qt_registro_w <> 0) then
			ds_retorno_w := get_contido('4');
		end if;

		if (ds_retorno_w = 'N') then
			select	count(*)
			into STRICT	qt_registro_w
			from	prescr_solucao
			where	nr_prescricao	= nr_prescricao_p
			and		coalesce(ie_suspenso, 'N')	<> 'S'
			and		coalesce(ie_hemodialise,'N')	= 'S';

			if (qt_registro_w <> 0) then
				ds_retorno_w := get_contido('4');
			end if;
		end if;
	end if;
end if;

if (ds_retorno_w = 'N') then
	Select sum(ct)
	into STRICT	Qt_registro_w
	from (	SELECT	count(*) ct

		from	NUT_PACIENTE
		where	nr_prescricao	= nr_prescricao_p
		and	coalesce(ie_suspenso, 'N')	<> 'S'
		
union all

		SELECT	count(*)
		from	NUT_PAC
		where	nr_prescricao	= nr_prescricao_p
		and 	coalesce(ie_npt_adulta,'N')  = 'S'
		and	coalesce(ie_suspenso, 'N')	<> 'S'
	) alias7;

	if (Qt_registro_w <> 0) then
		ds_retorno_w := get_contido('2');
	end if;
end if;

if (ds_retorno_w = 'N') then
	select	count(*)
	into STRICT	Qt_registro_w
	from	NUT_PAC
	where	nr_prescricao	= nr_prescricao_p
	and 	coalesce(ie_npt_adulta,'N') <> 'S'
	and	coalesce(ie_suspenso, 'N')	<> 'S';

	if (Qt_registro_w <> 0) then
		ds_retorno_w := get_contido('2');
	end if;
end if;

return ds_retorno_w;

end;
$body$
