SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_itens_processo_adep (nr_atendimento_p bigint, nr_seq_processo_p bigint) RETURNS varchar AS $body$
DECLARE

 
vl_retorno_w	varchar(4000);
dt_leitura_w	timestamp;
dt_preparo_w	timestamp;
ds_material_w	varchar(100);

c01 CURSOR FOR 
SELECT	substr(obter_dados_horario(nr_seq_horario,'DI'),1,100) ds_material 
from	adep_processo_item 
where	nr_seq_processo = nr_seq_processo_p 
order by 
	ds_material;


BEGIN 
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and (nr_seq_processo_p IS NOT NULL AND nr_seq_processo_p::text <> '') then 
 
	select	CASE WHEN count(*)=0 THEN  'N'  ELSE 'S' END  
	into STRICT	vl_retorno_w 
	from	adep_processo 
	where	nr_sequencia = nr_seq_processo_p;
 
	if (vl_retorno_w = 'S') then 
 
		select	validar_processo_atendimento(nr_atendimento_p, nr_seq_processo_p) 
		into STRICT	vl_retorno_w 
		;
 
		if (vl_retorno_w = 'S') then 
	 
			vl_retorno_w := null;
 
			select	dt_leitura, 
				dt_preparo 
			into STRICT	dt_leitura_w, 
				dt_preparo_w 
			from	adep_processo 
			where	nr_sequencia = nr_seq_processo_p;
 
			if (coalesce(dt_leitura_w::text, '') = '') then 
 
				vl_retorno_w := '#@Este processo ainda não foi lido por barras.';
 
			elsif (coalesce(dt_preparo_w::text, '') = '') then 
 
				vl_retorno_w := '#@Este processo ainda não foi preparado.';
 
			end if;
 
			if (coalesce(vl_retorno_w::text, '') = '') then 
 
				open c01;
				loop 
				fetch c01 into ds_material_w;
				EXIT WHEN NOT FOUND; /* apply on c01 */
					begin 
					vl_retorno_w := vl_retorno_w || ds_material_w || chr(10);
					end;
				end loop;
				close c01;
			 
			end if;
 
		else 
	 
			vl_retorno_w := '#@Este processo não pretence a este atendimento.';
			 
		end if;
 
	else 
	 
		vl_retorno_w := '#@Processo inválido.';
 
	end if;
 
end if;
 
return vl_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
