SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_itens_rotina_especial (nr_seq_rotina_p bigint, ie_quebra_linha_p text) RETURNS varchar AS $body$
DECLARE


ds_componentes_w	varchar(255);
ie_separador_w		varchar(3);

c01 CURSOR FOR
SELECT a.ds_prescricao ds_prescricao
  from procedimento_rotina a
 where a.nr_seq_rotina = nr_seq_rotina_p

union

SELECT b.ds_prescricao ds_prescricao
  from exame_lab_rotina b
 where b.nr_seq_rotina = nr_seq_rotina_p
 order by ds_prescricao;

BEGIN

ie_separador_w := chr(13)||chr(10);

if (nr_seq_rotina_p IS NOT NULL AND nr_seq_rotina_p::text <> '') then
	if (ie_quebra_linha_p = 'N') then
		ie_separador_w := ',';
	end if;
	
	for r_c01_w in c01
	loop		
		if (length(ds_componentes_w || r_c01_w.ds_prescricao || ie_separador_w) <= 255) then
			ds_componentes_w := substr(ds_componentes_w || r_c01_w.ds_prescricao || ie_separador_w, 1, 255);
		end if;
	end loop;
	
end if;

return ds_componentes_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
