CREATE OR REPLACE FUNCTION obter_ivc_ativo_atend (nr_atendimento_p bigint) RETURNS bigint AS $body$
DECLARE


nr_sequencia_w		bigint;


BEGIN

select	max(c.nr_sequencia)
into STRICT	nr_sequencia_w
from	prescr_medica a,
    	prescr_procedimento b,
		adep_irrigacao c
where	a.nr_prescricao = b.nr_prescricao
and		b.nr_prescricao = c.nr_prescricao
and		c.nr_atendimento = nr_atendimento_p
and   	coalesce(a.dt_suspensao::text, '') = ''
and   	coalesce(b.dt_suspensao::text, '') = ''
and		c.ie_status_ivc	= 'I';

if (coalesce(nr_sequencia_w,0) = 0) then
	select	max(c.nr_sequencia)
	into STRICT	nr_sequencia_w
	from	prescr_medica a,
        prescr_procedimento b,
        adep_irrigacao c
where	a.nr_prescricao = b.nr_prescricao
and		b.nr_prescricao = c.nr_prescricao
and		c.nr_atendimento = nr_atendimento_p
and   	coalesce(a.dt_suspensao::text, '') = ''
and   	coalesce(b.dt_suspensao::text, '') = ''
and		c.ie_status_ivc	= 'P';
end if;

return nr_sequencia_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
