CREATE OR REPLACE FUNCTION obter_juros_multa_pf_pj (cd_estabelecimento_p bigint, cd_pessoa_fisica_p text, cd_cgc_p text, ie_juro_multa_p text) RETURNS bigint AS $body$
DECLARE


/*

'J' - juros
'M' - multa

*/
pr_juro_padrao_w		double precision;
pr_multa_padrao_w		double precision;
pr_juro_pj_w			double precision;
pr_multa_pj_w			double precision;
pr_juro_w			double precision;
pr_multa_w			double precision;
pr_resultado_w			double precision;
ie_juros_multa_pj_w		varchar(5);

c01 CURSOR FOR
SELECT	a.tx_juros,
	a.tx_multa
from	regra_taxa_pessoa a
where	coalesce(a.cd_pessoa_fisica::text, '') = ''
and	a.cd_cgc		= cd_cgc_p

union

select	a.tx_juros,
	a.tx_multa
from	regra_taxa_pessoa a
where	coalesce(a.cd_cgc::text, '') = ''
and	a.cd_pessoa_fisica	= cd_pessoa_fisica_p

union

select	a.tx_juros,
	a.tx_multa
from	regra_taxa_pessoa a
where	not exists (select	1
	from	regra_taxa_pessoa x
	where (x.cd_cgc = cd_cgc_p or x.cd_pessoa_fisica = cd_pessoa_fisica_p))
and	coalesce(cd_pessoa_fisica::text, '') = ''
and	coalesce(cd_cgc::text, '') = '';


BEGIN

select	pr_juro_padrao,
	pr_multa_padrao,
	pr_juro_pj,
	pr_multa_pj,
	ie_juros_multa_pj
into STRICT	pr_juro_padrao_w,
	pr_multa_padrao_w,
	pr_juro_pj_w,
	pr_multa_pj_w,
	ie_juros_multa_pj_w
from	parametro_contas_receber
where	cd_estabelecimento	= cd_estabelecimento_p;

pr_juro_w	:= pr_juro_padrao_w;
pr_multa_w	:= pr_multa_padrao_w;

if (cd_cgc_p IS NOT NULL AND cd_cgc_p::text <> '') then

	if (coalesce(pr_juro_pj_w,0) > 0) or (ie_juros_multa_pj_w = 'S') then
		pr_juro_w	:= pr_juro_pj_w;
	end if;

	if (coalesce(pr_multa_pj_w,0) > 0) or (ie_juros_multa_pj_w = 'S') then
		pr_multa_w	:= pr_multa_pj_w;
	end if;

end if;

open c01;
loop
fetch c01 into
	pr_juro_w,
	pr_multa_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
end loop;
close c01;

pr_resultado_w			:= 0;
if (upper(ie_juro_multa_p) = 'M') then
	pr_resultado_w		:= pr_multa_w;
elsif (upper(ie_juro_multa_p) = 'J') then
	pr_resultado_w		:= pr_juro_w;
end if;

return	pr_resultado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
