SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_juros_multa_prev_tit_rec (nr_titulo_p bigint, ie_tit_valor_p text) RETURNS bigint AS $body$
DECLARE


/*
ie_tit_valor_p	J - Juros
		M - Multa
		T - Vl Total
*/
dt_pagamento_previsto_w	titulo_receber.dt_pagamento_previsto%type;
vl_saldo_titulo_w	titulo_receber.vl_saldo_titulo%type;
vl_juros_w		double precision;
vl_multa_w		double precision;
ie_base_juros_multa_w	varchar(1);


BEGIN

if (nr_titulo_p IS NOT NULL AND nr_titulo_p::text <> '') then

	ie_base_juros_multa_w := obter_param_usuario(801, 184, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, ie_base_juros_multa_w);

	select	dt_pagamento_previsto
	into STRICT	dt_pagamento_previsto_w
	from	titulo_receber
	where	nr_titulo = nr_titulo_p;

	if	((ie_base_juros_multa_w			= 'S') or
		 ((ie_base_juros_multa_w		= 'M') and (trunc(dt_pagamento_previsto_w)	> trunc(clock_timestamp())))) then
		select	coalesce(obter_juros_multa_titulo(nr_titulo,dt_pagamento_previsto_w,'R','J'),0),
			coalesce(obter_juros_multa_titulo(nr_titulo,dt_pagamento_previsto_w,'R','M'),0),
			vl_saldo_titulo
		into STRICT	vl_juros_w,
			vl_multa_w,
			vl_saldo_titulo_w
		from	titulo_receber
		where	nr_titulo	= nr_titulo_p;
	else
		select	coalesce(obter_juros_multa_titulo(nr_titulo,clock_timestamp(),'R','J'),0),
			coalesce(obter_juros_multa_titulo(nr_titulo,clock_timestamp(),'R','M'),0),
			vl_saldo_titulo
		into STRICT	vl_juros_w,
			vl_multa_w,
			vl_saldo_titulo_w
		from	titulo_receber
		where	nr_titulo	= nr_titulo_p;
	end if;

end if;

if (ie_tit_valor_p = 'J') then
	return vl_juros_w;
elsif (ie_tit_valor_p = 'M') then
	return vl_multa_w;
elsif (ie_tit_valor_p = 'T') then
	return vl_saldo_titulo_w + vl_juros_w + vl_multa_w;
else
	return null;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
